            $.ajax({
                url: "student/" + id,
                method: "GET",
                beforeSend: function (xhr) {
                    $(".fetched-data").html("<div class='fa fa-spin fa-spinner'></div>");
                },
                success: function (data) {
                    var html = '<table class="table table-striped">' +
                            '<tr>' +
                            '<td width="10%">NIM</td>' +
                            '<td width="5%">:</td>' +
