                            '<tr>' +
                            '<td>Name</td>' +
                            '<td>:</td>' +
                            '<td>' + data.name + '</td>' +
                            '</tr>' +
                            '<tr>' +
                            '<td>Phone</td>' +
                            '<td>:</td>' +
                            '<td>' + data.phone + '</td>' +
                            '</tr>' +
                            '<tr>' +
                            '<td>Class</td>' +
                            '<td>:</td>' +
                            '<td><strong><a href="{{url("class")}}/'+data.klas.id+'/edit">' + data.klas.name + '</a></strong></td>' +
                            '</tr>' +
                            '<tr>' +
                            '<td>Address</td>' +
                            ' <td>:</td>' +
                            '<td>' + data.address + '</td>' +
                            ' </tr>' +
                            '</table>';
                    $(".fetched-data").html(html);
                }
            });
        });
    });
</script>
